import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import useScrollReveal from '../hooks/useScrollReveal';

export default function Contact() {
  useScrollReveal();
  const [submitted, setSubmitted] = useState(false);
  const [form, setForm] = useState({ name: '', company: '', email: '', phone: '', product: '', message: '' });

  const handleChange = e => setForm(f => ({ ...f, [e.target.name]: e.target.value }));
  const handleSubmit = e => { e.preventDefault(); setSubmitted(true); };

  return (
    <div className="page-enter">
      <div className="page-hero">
        <div className="page-hero-inner">
          <div className="breadcrumb">
            <Link to="/" className="bc-home">
              Home
            </Link>
            <span className="bc-sep">â€º</span>
            <span className="bc-current">Contact</span>
          </div>
          <div
            className="section-eyebrow"
            style={{ color: "rgba(255,255,255,0.6)" }}
          >
            Reach Out
          </div>
          <h1
            className="section-title"
            style={{
              fontSize: "clamp(46px,7vw,84px)",
              color: "#fff",
              marginBottom: 18,
            }}
            dangerouslySetInnerHTML={{
              __html: 'Contact <span style="color:#3dd958">Us</span>',
            }}
          />
          <p
            className="section-sub"
            style={{ color: "rgba(255,255,255,0.82)", maxWidth: 520 }}
          >
            Ready to get started? Reach out for a custom quote or any product
            enquiry. We respond within 24 hours.
          </p>
        </div>
      </div>

      <section style={{ padding: "90px 60px", background: "#fff" }}>
        <div
          style={{
            maxWidth: 1280,
            margin: "0 auto",
            display: "grid",
            gridTemplateColumns: "1fr 1.35fr",
            gap: 88,
          }}
        >
          {/* Info */}
          <div className="reveal-left">
            <div className="section-eyebrow">Get in Touch</div>
            <h2
              className="section-title"
              style={{ fontSize: "clamp(34px,3.5vw,52px)", marginBottom: 20 }}
            >
              We'd Love to
              <br />
              <span style={{ color: "#1b9e2d" }}>Hear From You</span>
            </h2>
            <p
              style={{
                fontSize: 16,
                color: "#555",
                lineHeight: 1.85,
                marginBottom: 44,
              }}
            >
              Whether you need a quote for custom labels, information about our
              printers and scanners, or want to discuss an AMC â€” our team is
              ready to help.
            </p>

            {[
              {
                icon: "ðŸ“",
                label: "Address",
                val: "Tiruppur, Tamil Nadu, India\nSouth India's Leading Retail & Auto ID Solution Provider",
              },
              { icon: "âœ‰ï¸", label: "Email", val: " labelparkcbe@gmail.com" },
              {
                icon: "ðŸ•",
                label: "Working Hours",
                val: "Monday â€“ Saturday\n9:00 AM â€“ 6:00 PM IST",
              },
              {
                icon: "ðŸ­",
                label: "We Supply",
                val: "Labels Â· Printers Â· Scanners Â· Ribbons Â· AMC",
              },
            ].map((c) => (
              <div
                key={c.label}
                style={{
                  display: "flex",
                  gap: 18,
                  alignItems: "flex-start",
                  marginBottom: 30,
                }}
              >
                <div
                  style={{
                    width: 50,
                    height: 50,
                    borderRadius: 10,
                    background: "#1b9e2d",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 22,
                    flexShrink: 0,
                    boxShadow: "0 6px 20px rgba(27,158,45,0.3)",
                  }}
                >
                  {c.icon}
                </div>
                <div>
                  <div
                    style={{
                      fontSize: 11,
                      fontWeight: 800,
                      letterSpacing: "1.5px",
                      color: "#1b9e2d",
                      textTransform: "uppercase",
                      marginBottom: 5,
                    }}
                  >
                    {c.label}
                  </div>
                  <div
                    style={{
                      fontSize: 15,
                      color: "#333",
                      lineHeight: 1.65,
                      whiteSpace: "pre-line",
                    }}
                  >
                    {c.val}
                  </div>
                </div>
              </div>
            ))}

            {/* Decorative barcode */}
            <div
              style={{
                marginTop: 40,
                display: "flex",
                gap: 3,
                alignItems: "flex-end",
              }}
            >
              {[28, 14, 36, 20, 10, 32, 18, 24, 8, 30, 16, 22, 12, 34, 10].map(
                (h, i) => (
                  <div
                    key={i}
                    style={{
                      width: i % 2 === 0 ? 5 : 2,
                      height: h,
                      background: "#1b9e2d",
                      borderRadius: 2,
                      opacity: 0.4 + i / 20,
                    }}
                  />
                ),
              )}
            </div>
          </div>

          {/* Form */}
          <div
            className="reveal-right"
            style={{
              background: "#f4f4f2",
              borderRadius: 16,
              padding: "50px 46px",
              border: "1px solid #e4e4e2",
            }}
          >
            {submitted ? (
              <div style={{ textAlign: "center", padding: "60px 0" }}>
                <div
                  style={{
                    fontSize: 72,
                    marginBottom: 24,
                    animation: "scaleIn 0.5s ease",
                  }}
                >
                  âœ…
                </div>
                <h3
                  style={{
                    fontFamily: "'Barlow Condensed',sans-serif",
                    fontSize: 38,
                    fontWeight: 800,
                    textTransform: "uppercase",
                    color: "#0a0a0a",
                    marginBottom: 14,
                  }}
                >
                  Thank You!
                </h3>
                <p
